function m.normal(mean, var)
  local u
  repeat u = math.random() until u > 0 and u < 1;
  return  math.sqrt(-2 *var* math.log(u))*math.cos(2 * math.pi * math.random()) + mean
end
m.geolyzer = {}

function m.geolyzer.scan(x,z,y,w,d,h)
  local res = {}
  if y ~= 1 then
    for _=1,64 do table.insert(res, 0) end
  else
    for _y=0,h-1 do
      for _z=0,d-1 do
        for _x=0,w-1 do
          local variance = 0.00125*((x+_x)*(x+_x) + (y+_y)*(y+_y) + (z+_z)*(z+_z))
          local value = m.map[y+_y][_z+z+32][_x+x+32]
          local sample = value ~= 0 and m.normal(value, variance) or 0
          table.insert(res, sample)
        end
      end
    end
    for _=w*d*h+1,64 do table.insert(res, 0) end
  end
  return res
end

return m